'use strict';

import {EventEmitter} from 'events';
var Category = require('../../sqldb').Category;
var CategoryEvents = new EventEmitter();

// Set max event listeners (0 == unlimited)
CategoryEvents.setMaxListeners(0);

// Model events
var events = {
  'afterCreate': 'save',
  'afterUpdate': 'save',
  'afterDestroy': 'remove'
};

// Register the event emitter to the model events
for (var e in events) {
  var event = events[e];
  Category.hook(e, emitEvent(event));
}

function emitEvent(event) {
  return function(doc, options, done) {
    CategoryEvents.emit(event + ':' + doc._id, doc);
    CategoryEvents.emit(event, doc);
    done(null);
  }
}

export default CategoryEvents;

'use strict';

var app = require('../..');
import request from 'supertest';

var newCategory;

describe('Category API:', function() {

  describe('GET /api/categorys', function() {
    var categorys;

    beforeEach(function(done) {
      request(app)
        .get('/api/categorys')
        .expect(200)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          categorys = res.body;
          done();
        });
    });

    it('should respond with JSON array', function() {
      categorys.should.be.instanceOf(Array);
    });

  });

  describe('POST /api/categorys', function() {
    beforeEach(function(done) {
      request(app)
        .post('/api/categorys')
        .send({
          name: 'New Category',
          info: 'This is the brand new category!!!'
        })
        .expect(201)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          newCategory = res.body;
          done();
        });
    });

    it('should respond with the newly created category', function() {
      newCategory.name.should.equal('New Category');
      newCategory.info.should.equal('This is the brand new category!!!');
    });

  });

  describe('GET /api/categorys/:id', function() {
    var category;

    beforeEach(function(done) {
      request(app)
        .get('/api/categorys/' + newCategory._id)
        .expect(200)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          category = res.body;
          done();
        });
    });

    afterEach(function() {
      category = {};
    });

    it('should respond with the requested category', function() {
      category.name.should.equal('New Category');
      category.info.should.equal('This is the brand new category!!!');
    });

  });

  describe('PUT /api/categorys/:id', function() {
    var updatedCategory;

    beforeEach(function(done) {
      request(app)
        .put('/api/categorys/' + newCategory._id)
        .send({
          name: 'Updated Category',
          info: 'This is the updated category!!!'
        })
        .expect(200)
        .expect('Content-Type', /json/)
        .end(function(err, res) {
          if (err) {
            return done(err);
          }
          updatedCategory = res.body;
          done();
        });
    });

    afterEach(function() {
      updatedCategory = {};
    });

    it('should respond with the updated category', function() {
      updatedCategory.name.should.equal('Updated Category');
      updatedCategory.info.should.equal('This is the updated category!!!');
    });

  });

  describe('DELETE /api/categorys/:id', function() {

    it('should respond with 204 on successful removal', function(done) {
      request(app)
        .delete('/api/categorys/' + newCategory._id)
        .expect(204)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          done();
        });
    });

    it('should respond with 404 when category does not exist', function(done) {
      request(app)
        .delete('/api/categorys/' + newCategory._id)
        .expect(404)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          done();
        });
    });

  });

});

'use strict';

import _ from 'lodash';
var Promise = require("bluebird");
import TreeTable from '../../sqldb/treeTable';
import {UserRole} from '../../sqldb/';
import {User} from '../../sqldb/';
import {Space} from '../../sqldb/';

export default function(sequelize, DataTypes) {
	return sequelize.define('Role', {
		_id: {
			type: DataTypes.INTEGER,
			allowNull: false,
			primaryKey: true,
			autoIncrement: true
		},
		parentId: {
			type: DataTypes.INTEGER,
			defaultValue: -1
		},
		spaceId: {
			type: DataTypes.INTEGER,
			defaultValue: -1
		},
		appId: {
			type: DataTypes.INTEGER,
			defaultValue: -1
		},
		name: DataTypes.STRING,
		alias: DataTypes.STRING,
		fullname: DataTypes.STRING,
		owner: DataTypes.STRING,
		ownerId: {
			type: DataTypes.INTEGER,
			defaultValue: -1
		},
		active: DataTypes.BOOLEAN
	}, {
		getterMethods: {
			roleName: function(){
				return this.fullname.slice(5);
			},
			name: function(){
				return this.fullname.slice(5);
			}
		},
		classMethods: {
			getRoot: function() {
				return this
					.findOrCreate({
						where: {
							name: 'root',
							fullname: 'root'
						},
						defaults: {
							alias: 'root'
						}
					})
					.spread(function(entity, created) {
						return Promise.resolve(entity);
					});
			},
			getCustomer: function(context) {

				//console.log('in getTypeRoot typeData:', typeData);

				var Model = this;

				return this.getRoot().then(function(root) {
					var whereData = {};

					if (typeof context === 'object') {
						for (var key in context) {
							if (key.toLowerCase() === 'owner' || key.toLowerCase() === 'ownertype') {
								whereData.owner = context[key];
							}
							if (key.toLowerCase() === 'appid' || key.toLowerCase() === 'spaceid' || key.toLowerCase() === 'ownerid') {
								whereData.owner = context[key];
							}
						}
					}

					whereData.name = 'customer';
					whereData.fullname = 'root.customer';
					whereData.parentId = 1;

					//console.log('getTypeRoot root: ', JSON.stringify(root));

					//console.log('getTypeRoot whereData: ', JSON.stringify(whereData));

					return Model.findOrCreate({
						where: whereData,
						defaults: {
							alias: 'customer'
						}
					})
						.spread(function(entity, created) {
							//console.log('getTypeRoot created: ', created);
							return Promise.resolve(entity);
						});
				});

			},
			getMember: function(context) {

				var Model = this;

				return this.getRoot().then(function(root) {
					var whereData = {};

					if (typeof context === 'object') {
						for (var key in context) {
							if (key.toLowerCase() === 'owner' || key.toLowerCase() === 'ownertype') {
								whereData.owner = context[key];
							}
							if (key.toLowerCase() === 'appid' || key.toLowerCase() === 'spaceid' || key.toLowerCase() === 'ownerid') {
								whereData.owner = context[key];
							}
						}
					}

					whereData.name = 'member';
					whereData.fullname = 'root.member';
					whereData.parentId = 1;

					return Model.findOrCreate({
						where: whereData,
						defaults: {
							alias: 'member'
						}
					})
						.spread(function(entity, created) {
							return Promise.resolve(entity);
						});
				});
			},
			getAdmin: function(context) {

				var Model = this;

				return this.getRoot().then(function(root) {
					var whereData = {};

					if (typeof context === 'object') {
						for (var key in context) {
							if (key.toLowerCase() === 'owner' || key.toLowerCase() === 'ownertype') {
								whereData.owner = context[key];
								//delete categoryData[key];
							}
							if (key.toLowerCase() === 'appid' || key.toLowerCase() === 'spaceid' || key.toLowerCase() === 'ownerid') {
								whereData.owner = context[key];
								//delete categoryData[key];
							}
						}
					}

					whereData.name = 'admin';
					whereData.fullname = 'root.admin';
					whereData.parentId = 1;

					return Model.findOrCreate({
						where: whereData,
						defaults: {
							alias: 'admin'
						}
					})
						.spread(function(entity, created) {
							return Promise.resolve(entity);
						});
				});
			},
			getUserRoles: function(context) {
				var whereData = {};
				if(typeof context === 'object'){
					for(var key in context){
						if( key.toLowerCase() === 'spaceid' || key.toLowerCase() === 'userid'){
							whereData.spaceId = context[key];
						}
					}
				}

				if(whereData.hasOwnProperty('spaceId') && whereData.hasOwnProperty('appId')){
					UserRole.belongsTo(User);
					UserRole.belongsTo(Space);
					UserRole.belongsTo(Role);
					return UserRole.findAll({
						include: [{all: true}],
						where: whereData
					})
				}

				//otherwhise, return error
				return Promise.reject(new Error('fail to run function!'));
			}
		},
		instanceMethods:{
			addChild: function(childData){
				var treeObj = new TreeTable();
				return treeObj.addChild(this,childData);
			},
			getChildren: function(childData){
				var treeObj = new TreeTable();
				return treeObj.getChildren(this,childData);
			},
			getChild: function(childData){
				var treeObj = new TreeTable();
				return treeObj.getChild(this,childData);
			},
			getParent: function(recursive){
				var treeObj = new TreeTable();
				return treeObj.getParent(this,recursive);
			},
			childCount: function(){
				var treeObj = new TreeTable();
				return treeObj.childCount(this);
			},
			isParentOf: function(childObj,recursive){
				var treeObj = new TreeTable();
				return treeObj.isParentOf(childObj,this,recursive);
			},
			isChildOf: function(parentObj,recursive){
				var treeObj = new TreeTable();
				return treeObj.isChildOf(parentObj,this,childData);
			}
		}
	});
}

