/**
 * App model events
 */

'use strict';

import {EventEmitter} from 'events';
var App = require('../../sqldb').App;
var AppEvents = new EventEmitter();

// Set max event listeners (0 == unlimited)
AppEvents.setMaxListeners(0);

// Model events
var events = {
  'afterCreate': 'save',
  'afterUpdate': 'save',
  'afterDestroy': 'remove'
};

// Register the event emitter to the model events
for (var e in events) {
  var event = events[e];
  App.hook(e, emitEvent(event));
}

function emitEvent(event) {
  return function(doc, options, done) {
    AppEvents.emit(event + ':' + doc._id, doc);
    AppEvents.emit(event, doc);
    done(null);
  }
}

export default AppEvents;

'use strict';

var app = require('../..');
import request from 'supertest';

var newApp;

describe('App API:', function() {

  describe('GET /api/apps', function() {
    var apps;

    beforeEach(function(done) {
      request(app)
        .get('/api/apps')
        .expect(200)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          apps = res.body;
          done();
        });
    });

    it('should respond with JSON array', function() {
      apps.should.be.instanceOf(Array);
    });

  });

  describe('POST /api/apps', function() {
    beforeEach(function(done) {
      request(app)
        .post('/api/apps')
        .send({
          name: 'New App',
          info: 'This is the brand new app!!!'
        })
        .expect(201)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          newApp = res.body;
          done();
        });
    });

    it('should respond with the newly created app', function() {
      newApp.name.should.equal('New App');
      newApp.info.should.equal('This is the brand new app!!!');
    });

  });

  describe('GET /api/apps/:id', function() {
    var app;

    beforeEach(function(done) {
      request(app)
        .get('/api/apps/' + newApp._id)
        .expect(200)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          app = res.body;
          done();
        });
    });

    afterEach(function() {
      app = {};
    });

    it('should respond with the requested app', function() {
      app.name.should.equal('New App');
      app.info.should.equal('This is the brand new app!!!');
    });

  });

  describe('PUT /api/apps/:id', function() {
    var updatedApp;

    beforeEach(function(done) {
      request(app)
        .put('/api/apps/' + newApp._id)
        .send({
          name: 'Updated App',
          info: 'This is the updated app!!!'
        })
        .expect(200)
        .expect('Content-Type', /json/)
        .end(function(err, res) {
          if (err) {
            return done(err);
          }
          updatedApp = res.body;
          done();
        });
    });

    afterEach(function() {
      updatedApp = {};
    });

    it('should respond with the updated app', function() {
      updatedApp.name.should.equal('Updated App');
      updatedApp.info.should.equal('This is the updated app!!!');
    });

  });

  describe('DELETE /api/apps/:id', function() {

    it('should respond with 204 on successful removal', function(done) {
      request(app)
        .delete('/api/apps/' + newApp._id)
        .expect(204)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          done();
        });
    });

    it('should respond with 404 when app does not exist', function(done) {
      request(app)
        .delete('/api/apps/' + newApp._id)
        .expect(404)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          done();
        });
    });

  });

});

'use strict';

import sqldb from '../../sqldb';
import _ from 'lodash';
var Promise = require('bluebird');

export default function(sequelize, DataTypes) {
  return sequelize.define('Nut', {
    _id: {
      type: DataTypes.INTEGER,
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    name: {
      type: DataTypes.STRING
    },
	appId: DataTypes.INTEGER,
	spaceId: DataTypes.INTEGER,
	alias: DataTypes.STRING,
    description: DataTypes.STRING,
	typeId: DataTypes.INTEGER,
    active: DataTypes.BOOLEAN
  },{
	  classMethods: {
		  addType: function(typeData){
			  return this.getType(typeData,true);
		  },
		  getType: function(typeData,autoCreated){
			  //console.log('in app Category:', Category);
			  //console.log('in app User:', User);
			  //console.log('in app sqldb:', sqldb);
			  var Category = sqldb.Category;
			  //var Category = sqldb.Category;
			  //console.log('in app Category:', Category);
			  if(typeof typeData === 'string' && isNaN(typeData)){
				  var typeName = typeData;

				  typeData = {};

				  typeData.name = typeName;

				  //console.log('in space getType');
				  //console.log('in space Category:', Category);
				  //console.log('space getType typeData:', JSON.stringfy(typeData));
			  }

			  if(typeof typeData === 'object' && !_.isEmpty(typeData)){
				typeData.owner = 'nut';
				var tyName = typeData.name;
			    if(tyName.substr(0,4).toLowerCase() !== 'nut.'){
				   tyName = 'nut.' + tyName;
			    }
				typeData.name = tyName;
				//console.log('app model typeData:', JSON.stringify(typeData));
			  	return Category.getType(typeData, true);
			  }

			  //otherwise return promise reject
			  sequelize.Promise.reject(new Error('fail to add type for nut!'));
		  },
		  addBulkNut: function(bulkNutData){

		  	var that = this;

		  	return Promise.each(bulkNutData, function(nutData){
		  		if(nutData.type && !nutData.typeId){
		  			var typeData = {
		  				name: nutData.type,
		  				spaceId: nutData.spaceId
		  			};

		  			return that.getType(typeData, true).then(function(type){
		  				nutData.typeId = type._id;
		  				//console.log('nutData:', nutData);
		  				return Promise.resolve(null);
		  			});
		  		} else {
		  			return Promise.resolve(null);
		  		}
		  	}).then(function(){
		  		//console.log('2 bulkNutData:', JSON.stringify(bulkNutData));
		  		return that.bulkCreate(bulkNutData);
		  	});

		  }
	  },
  });
}

/**
 * Broadcast updates to client when the model changes
 */

'use strict';

var AppEvents = require('./app.events');

// Model events to emit
var events = ['save', 'remove'];

export function register(socket) {
  // Bind model events to socket events
  for (var i = 0, eventsLength = events.length; i < eventsLength; i++) {
    var event = events[i];
    var listener = createListener('app:' + event, socket);

    AppEvents.on(event, listener);
    socket.on('disconnect', removeListener(event, listener));
  }
}


function createListener(event, socket) {
  return function(doc) {
    socket.emit(event, doc);
  };
}

function removeListener(event, listener) {
  return function() {
    AppEvents.removeListener(event, listener);
  };
}

