        //console.log(7);
        PermitRole.belongsTo(Permit, { as: 'permit' });
        //console.log(8);
        PermitRole.belongsTo(Role, { as: 'role' });
        //console.log(9);

        return Space.findAll({
            include: [
                {
                    model: Category, as: 'type'
                },
                {
                    model: Role, as: 'roles',
                    include: [
                        {
