                        {
                            model: Category, as: 'type'
                        },
                        {
                            model: Nut, as: 'nuts',
                            include: [
                                {
                                    model: PermitRole, as: 'permitRoles',
                                    where: {
                                        'owner': 'nut'
                                    },
                                    include: [
                                        {
                                            model: Permit, as: 'permit'
                                        },
                                        {
                                            model: Role, as: 'role',
                                            include: [
                                                {
                                                    model: User, as: 'users',
