                                                        _id: query.userId
                                                    }
                                                }
                                            ]
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                }
            ]
        }).then(function (spaces) {
            //console.log('spaces: ', JSON.stringify(spaces));
            return Promise.resolve(spaces);
        })
            .then(respondWithResult(res))
            .catch(handleError(res));
    }
}

