/**
 * Space model events
 */

'use strict';

import {EventEmitter} from 'events';
var Space = require('../../sqldb').Space;
var SpaceEvents = new EventEmitter();

// Set max event listeners (0 == unlimited)
SpaceEvents.setMaxListeners(0);

// Model events
var events = {
  'afterCreate': 'save',
  'afterUpdate': 'save',
  'afterDestroy': 'remove'
};

// Register the event emitter to the model events
for (var e in events) {
  var event = events[e];
  Space.hook(e, emitEvent(event));
}

function emitEvent(event) {
  return function(doc, options, done) {
    SpaceEvents.emit(event + ':' + doc._id, doc);
    SpaceEvents.emit(event, doc);
    done(null);
  }
}

export default SpaceEvents;

'use strict';

var app = require('../..');
import request from 'supertest';

var newSpace;

describe('Space API:', function() {

  describe('GET /api/spaces', function() {
    var spaces;

    beforeEach(function(done) {
      request(app)
        .get('/api/spaces')
        .expect(200)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          spaces = res.body;
          done();
        });
    });

    it('should respond with JSON array', function() {
      spaces.should.be.instanceOf(Array);
    });

  });

  describe('POST /api/spaces', function() {
    beforeEach(function(done) {
      request(app)
        .post('/api/spaces')
        .send({
          name: 'New Space',
          info: 'This is the brand new space!!!'
        })
        .expect(201)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          newSpace = res.body;
          done();
        });
    });

    it('should respond with the newly created space', function() {
      newSpace.name.should.equal('New Space');
      newSpace.info.should.equal('This is the brand new space!!!');
    });

  });

  describe('GET /api/spaces/:id', function() {
    var space;

    beforeEach(function(done) {
      request(app)
        .get('/api/spaces/' + newSpace._id)
        .expect(200)
        .expect('Content-Type', /json/)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          space = res.body;
          done();
        });
    });

    afterEach(function() {
      space = {};
    });

    it('should respond with the requested space', function() {
      space.name.should.equal('New Space');
      space.info.should.equal('This is the brand new space!!!');
    });

  });

  describe('PUT /api/spaces/:id', function() {
    var updatedSpace;

    beforeEach(function(done) {
      request(app)
        .put('/api/spaces/' + newSpace._id)
        .send({
          name: 'Updated Space',
          info: 'This is the updated space!!!'
        })
        .expect(200)
        .expect('Content-Type', /json/)
        .end(function(err, res) {
          if (err) {
            return done(err);
          }
          updatedSpace = res.body;
          done();
        });
    });

    afterEach(function() {
      updatedSpace = {};
    });

    it('should respond with the updated space', function() {
      updatedSpace.name.should.equal('Updated Space');
      updatedSpace.info.should.equal('This is the updated space!!!');
    });

  });

  describe('DELETE /api/spaces/:id', function() {

    it('should respond with 204 on successful removal', function(done) {
      request(app)
        .delete('/api/spaces/' + newSpace._id)
        .expect(204)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          done();
        });
    });

    it('should respond with 404 when space does not exist', function(done) {
      request(app)
        .delete('/api/spaces/' + newSpace._id)
        .expect(404)
        .end((err, res) => {
          if (err) {
            return done(err);
          }
          done();
        });
    });

  });

});

'use strict';

import sqldb from '../../sqldb';
import _ from 'lodash';
var Promise = require("bluebird");

export default function (sequelize, DataTypes) {
	return sequelize.define('Space', {
		_id: {
			type: DataTypes.INTEGER,
			allowNull: false,
			primaryKey: true,
			autoIncrement: true
		},
		name: {
			type: DataTypes.STRING,
			unique: {
				msg: 'The specified space name is already in use.'
			}
		},
		alias: DataTypes.STRING,
		description: DataTypes.STRING,
		typeId: DataTypes.INTEGER,
		active: DataTypes.BOOLEAN
	}, {
			getterMethods: {
				profile: function () {
					return {
						alias: this.alias,
						description: this.description
					}
				}
				/*
				type: function(){
					return Category.findById(typeId);
				}
				*/
			},
			classMethods: {
				getUserSpaces: function (userId) {
					UserRole.belongsTo(User);
					UserRole.blongsTo(this);
					return UserRole.findAll({
						attributes: ['spaceId', 'userId'],
						where: { userId: userId },
						include: [User, this],
						group: ['spaceId']
					});
				},
				addType: function (typeData) {
					return this.getType(typeData, true);
				},
				getType: function (typeData, autoCreated) {
					//console.log('in space Category:', typeData);
					//console.log('in space User:', User);
					//console.log('in space sqldb:', sqldb);
					var Category = sqldb.Category;
					//var Category = sqldb.Category;
					//console.log('in space Category:', Category);
					if (typeof typeData === 'string' && isNaN(typeData)) {
						var typeName = typeData;

						typeData = {};

						typeData.name = typeName;

						//console.log('in space getType');
						//console.log('in space Category:', Category);
						//console.log('space getType typeData:', JSON.stringfy(typeData));
					}

					if (!isNaN(typeData) && typeData > 0) {
						return Category.findById(typeData);
					}

					if (typeof typeData === 'object' && !_.isEmpty(typeData)) {
						typeData.owner = 'space';
						var tyName = typeData.name;
						if (tyName.substr(0, 5).toLowerCase() !== 'space') {
							tyName = 'space.' + tyName;
						}
						typeData.name = tyName;
						//console.log('space model typeData:', JSON.stringify(typeData));
						return Category.getType(typeData, true);
					}

					//otherwise return promise reject
					sequelize.Promise.reject(new Error('fail to add type!'));
				},
				//this function can use to create
				//params: {name:xxx, alias:xxx, type:xxx, roles: [...]}
				//return space with type and roles
				add: function (spaceData) {
					var that = this;
					var typeId, spaceId;
					var Category = sqldb.Category;
					var Role = sqldb.Role;

					this.hasMany(Role, { as: 'roles' });
					this.belongsTo(Category, { as: 'type' });

					return new Promise(function (resolve, reject) {
						if (spaceData.typeId) {
							typeId = spaceData.typeId;
							return resolve(spaceData.typeId);
						} else if (spaceData.type) {
							if(spaceData.type.roles){
								spaceData.roles = spaceData.type.roles;
								delete spaceData.type.roles;
							}
							if(spaceData.type.apps){
								spaceData.apps = spaceData.type.apps;
								delete spaceData.type.roles;
							}
							return that.addType(spaceData.type).then(function (type) {
								typeId = type._id;
								return resolve(type._id);
							})
						}
						return resolve(null);
					}).then(function (typeId) {
						//console.log('typeId:', typeId);
						spaceData.typeId = typeId;
						return that.findOrCreate({
							where: {
								name: spaceData.name //spaceName must be unique name
							},
							defaults: spaceData
						})
					}).spread(function (space, created) {
						spaceId = space._id;
						//console.log('space', JSON.stringify(space));
						if (spaceData.roles) {
							return that.addRoles(spaceData.roles, space._id);
						}
						return Promise.resolve(null);
					}).then(function () {
						//console.log('spaceId:', spaceId);
						return that.find({
							where: {
								_id: spaceId
							},
							include: [
								{
									model: Category, as: 'type'
								},
								{
									model: Role, as: 'roles'
								}
							]
						}).then(function(space){
							//console.log('space:',JSON.stringify(space));
							return Promise.resolve(space);
						})
					})
				},

				addRoles: function (listRoleData, spaceId) {

					var that = this;
					var roles = [];

					return Promise.each(listRoleData, function (roleData) {

						return that.addRole(roleData, spaceId).then(function (role) {
							roles.push(role);
						})
					})
				},

				addRole: function (roleData, spaceId) {

					var Role = sqldb.Role;

					if(typeof roleData === 'string'){
						roleData = {
							name: roleData
						}
					}

					if (typeof roleData === 'object') {
						if (!isNaN(spaceId) && spaceId > 0) {
							roleData.spaceId = spaceId;
						}
						//console.log('roleData:',JSON.stringify(roleData));
						if (!roleData.spaceId || !roleData.name || roleData.name === "") {
							return Promise.reject('please provide spaceId and role name!');
						}
						return Role.addRole(roleData);
					} else {
						return Promise.reject('fail to add role!');
					}

				}
			},
			instanceMethods: {

				addRole: function (roleData) {

					var Role = sqldb.Role;

					if (typeof roleData === 'string') {
						roleData = {
							name: roleData
						}
					}

					roleData.spaceId = this._id;

					return Role.addRole(roleData);
				}

			},
			hooks: {

			}
		});
}

/**
 * Broadcast updates to client when the model changes
