'use strict';

import {User} from '../../sqldb';
import {UserRole} from '../../sqldb';
import {Role} from '../../sqldb';
import passport from 'passport';
import config from '../../config/environment';
import jwt from 'jsonwebtoken';

function validationError(res, statusCode) {
  statusCode = statusCode || 422;
  return function(err) {
    res.status(statusCode).json(err);
  }
}

function handleError(res, statusCode) {
  statusCode = statusCode || 500;
  return function(err) {
    res.status(statusCode).send(err);
  };
}

/**
 * Get list of users
 * restriction: 'admin'
 */
export function index(req, res) {

  User.belongsToMany(Role, { as: 'roles', through: UserRole});
  Role.belongsToMany(User, { as: 'users', through: UserRole});

  User.findAll({
    attributes: [
      '_id',
      'name',
      'loginId',
      'email',
      //'role',
      'provider'
    ],
    include: [
      {
        model: Role, as: 'roles'
      }
    ]
  })
    .then(users => {
      res.status(200).json(users);
    })
    .catch(handleError(res));
}

/**
 * Creates a new user
 */
export function create(req, res, next) {
  var newUser = User.build(req.body);
  newUser.setDataValue('provider', 'local');
  newUser.setDataValue('role', 'user');
  newUser.save()
    .then(function(user) {
      var token = jwt.sign({ _id: user._id }, config.secrets.session, {
        expiresIn: 60 * 60 * 5
      });
      res.json({ token });
    })
    .catch(validationError(res));
}

/**
 * Get a single user
 */
export function show(req, res, next) {
  var userId = req.params.id;

  User.belongsToMany(Role, { as: 'roles', through: UserRole});
  Role.belongsToMany(User, { as: 'users', through: UserRole});

  User.find({
    where: {
      _id: userId
    },
    include: [
      {
        model: Role, as: 'roles'
      }
    ]
  })
    .then(user => {
      if (!user) {
        return res.status(404).end();
      }
      res.json(user.profile);
    })
    .catch(err => next(err));
}

/**
 * Deletes a user
 * restriction: 'admin'
 */
export function destroy(req, res) {
  User.destroy({ _id: req.params.id })
    .then(function() {
      res.status(204).end();
    })
    .catch(handleError(res));
}

/**
 * Change a users password
 */
export function changePassword(req, res, next) {
  var userId = req.user._id;
  var oldPass = String(req.body.oldPassword);
  var newPass = String(req.body.newPassword);

  User.find({
    where: {
      _id: userId
    }
  })
    .then(user => {
      if (user.authenticate(oldPass)) {
        user.password = newPass;
        return user.save()
          .then(() => {
            res.status(204).end();
          })
          .catch(validationError(res));
      } else {
        return res.status(403).end();
      }
    });
}

/**
 * Get my info
 */
export function me(req, res, next) {
  var userId = req.user._id;
  //console.log('req header',req.headers);

  User.belongsToMany(Role, {as: 'roles', through: UserRole});
  Role.belongsToMany(User, {as: 'users', through: UserRole});

  User.find({
    where: {
      _id: userId
    },
    attributes: [
      '_id',
      'name',
      'email',
      'loginId',
      'role',
      'provider'
    ],
    include: [
      {
        model: Role, as: 'roles'
      }
    ]
  })
    .then(user => { // don't ever give out the password or salt
      //console.log('me user:', JSON.stringify(user));
      if (!user) {
        return res.status(401).end();
      }
      res.json(user);
    })
    .catch(err => next(err));
}

