/**
 * Using Rails-like standard naming convention for endpoints.
 * GET     /api/wechats              ->  index
 * POST    /api/wechats              ->  create
 * GET     /api/wechats/:id          ->  show
 * PUT     /api/wechats/:id          ->  update
 * DELETE  /api/wechats/:id          ->  destroy
 */

'use strict';

import _ from 'lodash';
import {Wechat} from '../../sqldb';

function respondWithResult(res, statusCode) {
  statusCode = statusCode || 200;
  return function(entity) {
    if (entity) {
      res.status(statusCode).json(entity);
    }
  };
}

function saveUpdates(updates) {
  return function(entity) {
    return entity.updateAttributes(updates)
      .then(updated => {
        return updated;
      });
  };
}

function removeEntity(res) {
  return function(entity) {
    if (entity) {
      return entity.destroy()
        .then(() => {
          res.status(204).end();
        });
    }
  };
}

function handleEntityNotFound(res) {
  return function(entity) {
    if (!entity) {
      res.status(404).end();
      return null;
    }
    return entity;
  };
}

function handleError(res, statusCode) {
  statusCode = statusCode || 500;
  return function(err) {
    res.status(statusCode).send(err);
  };
}

// Gets a list of Wechats
export function index(req, res) {
  Wechat.findAll()
    .then(respondWithResult(res))
    .catch(handleError(res));
}

// Gets a single Wechat from the DB
export function show(req, res) {
  Wechat.find({
    where: {
      _id: req.params.id
    }
  })
    .then(handleEntityNotFound(res))
    .then(respondWithResult(res))
    .catch(handleError(res));
}

// Creates a new Wechat in the DB
export function create(req, res) {
  Wechat.create(req.body)
    .then(respondWithResult(res, 201))
    .catch(handleError(res));
}

// Updates an existing Wechat in the DB
export function update(req, res) {
  if (req.body._id) {
    delete req.body._id;
  }
  Wechat.find({
    where: {
      _id: req.params.id
    }
  })
    .then(handleEntityNotFound(res))
    .then(saveUpdates(req.body))
    .then(respondWithResult(res))
    .catch(handleError(res));
}

// Deletes a Wechat from the DB
export function destroy(req, res) {
  Wechat.find({
    where: {
      _id: req.params.id
    }
  })
    .then(handleEntityNotFound(res))
    .then(removeEntity(res))
    .catch(handleError(res));
}

/**
 * Wechat model events
 */

'use strict';

import {EventEmitter} from 'events';
var Wechat = require('../../sqldb').Wechat;
var WechatEvents = new EventEmitter();

// Set max event listeners (0 == unlimited)
WechatEvents.setMaxListeners(0);

// Model events
var events = {
  'afterCreate': 'save',
  'afterUpdate': 'save',
  'afterDestroy': 'remove'
};

// Register the event emitter to the model events
for (var e in events) {
  var event = events[e];
  Wechat.hook(e, emitEvent(event));
}

function emitEvent(event) {
  return function(doc, options, done) {
    WechatEvents.emit(event + ':' + doc._id, doc);
    WechatEvents.emit(event, doc);
    done(null);
  }
}

export default WechatEvents;

